import "./App.css";
import Profile from "./component/profile/profile";
import pic from "./component/profile/myPhoto.jpg";
import pic2 from "./photo.jpg";

import React, { Component } from "react";
export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false
    };
  }
  toggleVisibility = () => {
    this.setState({
      isVisible: !this.state.isVisible
    });
  };

  render() {
    return (
      <div
        className="App"
        style={{
          backgroundImage: `url(${pic2})`,
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover"
        }}
      >
        <button className="visibility-btn" onClick={this.toggleVisibility}>
          {this.state.isVisible ? "Hide Profile" : "Show Profile"}
        </button>
        {this.state.isVisible ? (
          <Profile
            fullName=" WES ANDERSON "
            bio="Wes Anderson Bio"
            description="Wes Anderson is American film director, producer, screenwriter and occasional actor. He's known for such films as The Grand Budapest Hotel, The Royal Tenenbaums and Moonrise Kingdom. Anderson was born on May 1st, 1969 in Houston, Texas. His mother worked in real estate, and his father worked in advertising and public relations. He attended St. John's School followed by the University of Texas at Austin. He graduated in 1991 with a Bachelor of Arts degree in philosophy."
            profession="Wes Anderson's profession"
            job="Jackson became known as the King of Pop, 
        a nickname that Jackson's publicists embraced. 
        When Elizabeth Taylor presented him with the Soul Train Heritage Award in 1989,
        she called him the true king of pop, rock and soul. 
        President George H. W. Bush designated him the White House's Artist of the Decade. 
        From 1985 to 1990, Jackson donated $455,000 to the United Negro College Fund,
        and all profits from his single  Man in the Mirror  went to charity. 
        His rendition of  You Were There  at Sammy Davis Jr.'s 60th birthday 
        celebration won Jackson a second Emmy nomination. 
        Jackson was the bestselling artist of the 1980s. "
          >
            <div>
              <img src={pic} alt="Photo Of Micheal" />
            </div>
          </Profile>
        ) : null}
      </div>
    );
  }
}
